mapper.update:
update appointments set student_user_id = #{studentUserId}, teaching_assistant_user_id = #{teachingAssistantUserId}, group_id = #{groupId},
title = #{title}, content = #{content}, appointment_type = #{appointmentType}, start_time = #{startTime.stringValue}, end_time = #{endTime.stringValue}
where appointments.appointment_id = #{appointmentId};
